import React from "react";
import {useFormik} from 'formik'

function App() {
  const formik = useFormik({
    initialValues: {
      emailField: '',
      password: ''
    },
    onSubmit: values =>{
      alert('Login Successful')
      console.log('form: ', values);
    },
    validate: values =>{
      let errors = {};
      if(!values.emailField) {
        errors.emailField = 'Field required';
      } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.emailField)){
        errors.emailField = 'Username should be an email';
      }
      if(!values.password) errors.password = 'Field required';
      return errors
    }
  });

  return (
    <div>
      <form onSubmit={formik.handleSubmit}>
        <div>Email:</div>
        <input type="text" name="emailField" id="emailField" onChange={formik.handleChange} value={formik.values.emailField}></input>
        {formik.errors.emailField ? <div style={{color: 'red'}}>{formik.errors.emailField}</div> : null}
        <div>Password</div>
        <input type="text" name="password" onChange={formik.handleChange} value={formik.values.password}></input>
        {formik.errors.password ? <div style={{color: 'red'}}>{formik.errors.password}</div> : null}
        <br></br>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default App;
